import React, { Component } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import SideBar from '../components/SideBar/SideBar';
import axios from 'axios';
import {storage} from '../firebase/index';
class UpdateProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url_avatar: ""
        }
        this.handleUpload = this.handleUpload.bind(this);
    }
    handleUpload = (e) => {
        const files = e.target.files[0]
        const uploadTask = storage.ref(`images/${files.name}`).put(files);
        uploadTask.on('state_changed', 
            () => {
            // complete function ....
            storage.ref('images').child(files.name).getDownloadURL().then(url => {
                this.state.url_avatar = url;
                console.log(this.state.url_avatar)
            })
        });        
    }


    submit = (event) => {
        event.preventDefault();
    }

    render() {
        return (
            <div className="container-scroller">
            <Header/>
            <div className="container-fluid page-body-wrapper">
                <SideBar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="page-header">
                            <h3 className="page-title">
                                <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                <i className="mdi mdi-basket menu-icon" />
                                </span> Cập Nhật Thông Tin Nhà Hàng
                            </h3>
                        </div>
                        <div className="card">
                            <div className="card-body">
                                <form className="forms-sample" onSubmit={this.submit}>
                                    <div className="form-group">
                                        <label htmlFor="formFileMultiple" className="form-label">Giấy chứng nhận/chứng chỉ về VSATTP</label><br></br>
                                        <input className="form-control" name="image" required type="file" onChange={handleUpload}/>
                                    </div>

                                    <button type="submit" className="btn btn-gradient-primary mr-2">Cập nhật</button>
                                </form>
                            </div>
                        </div>
                    </div>
                    <Footer/>
                </div>
            </div>
        </div>
        );
    }
}
export default UpdateProfile;
