import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
class EditProduct extends Component {
    constructor(props) {
        super(props)
        this.state = {
          product: []
        };
    }
    componentDidMount() {
        let id = this.props.location.state.id;
        axios.get('http://127.0.0.1:8000/api/admin_product/'+id)
        .then(res => {
            this.setState({
                product: res.data
            });
        }).catch((error) => {
            console.log(error);
        })
    }
    
    onChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        if (nam === "img") {
            val = "image/" + event.target.files[0].name;
        }
        this.setState({ [nam]: val });
    }

    submit = (event) => {
        event.preventDefault();
        let updateProduct ={
            name:this.state.product.name,
            price:this.state.product.price,
            discount:this.state.product.discount,
            service_category_id:this.state.product.service_category_id,
            vendor_id: this.state.product.vendor_id,            
            description:this.state.product.description
        };

        alert(this.state.updateProduct.name);
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Product Management
                                </h3>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <h4 className="card-title">Edit Product</h4>
                                    <form className="forms-sample"onSubmit={this.submit}>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputName1">Name</label>
                                            <input type="text" className="form-control" id="exampleInputName1" name="name" value={this.state.product.name} onChange={(e) => this.onChangeHandler(e)}/> 
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputPrice">Price</label>
                                            <input type="number" className="form-control" id="exampleInputPrice"/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputDiscount">Discount</label>
                                            <input type="number" className="form-control" id="exampleInputDiscount"/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleSelectCategory">Category</label>
                                            <select className="form-control" id="exampleSelectCategory">
                                                <option>Category1</option>
                                            </select>
                                        </div>
                                        <div className="form-group">
                                            <label>Image upload</label>
                                            <input type="file" name="img[]" className="file-upload-default" />
                                            <div className="input-group col-xs-12">
                                            <input type="text" className="form-control file-upload-info" disabled/>
                                                <span className="input-group-append">
                                                    <button className="file-upload-browse btn btn-gradient-primary" type="button">Upload</button>
                                                </span>
                                            </div>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleDescription">Description</label>
                                            <textarea className="form-control" id="exampleDescription" rows={4} defaultValue={""} />
                                        </div>
                                        <button type="submit" className="btn btn-gradient-primary mr-2">Submit</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div> 
        );
    }
}

export default EditProduct;
