import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
class AddVendor extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name:"",
            phone:"",
            email: "",
            address: "",
            avatar: "",
            description: "",
            service_category_id: ""
        };
    }

    onChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        if (nam === "img") {
            val = "image/" + event.target.files[0].name;
        }
        this.setState({ [nam]: val });
    }

    submit = (event) => {
        alert("Active");
        event.preventDefault();
        let vendor = {
            name: this.state.name,
            phone: this.state.phone,
            email: this.state.email,
            address: this.state.address,
            avatar: this.state.avatar,
            description: this.state.description,
            service_category_id: this.state.service_category_id
           
        }
        console.log(vendor);
        axios.post('http://127.0.0.1:8000/api/admin_vendor/',vendor)
            .then((res) => {
                alert("Add successfully!");
                console.log(res);
                this.props.history.push("/vendor");
            }).catch((error) => {
                console.log(error)
            })
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Quản Lí Nhà Cung Cấp - Thêm Mới 
                                </h3>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <form className="forms-sample"onSubmit={this.submit}>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputName1">Name</label>
                                            <input type="text" className="form-control" id="exampleInputName1" name="name" value={this.state.name} onChange={(e) => this.onChangeHandler(e)}/> 
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputPhone">Phone</label>
                                            <input type="text" className="form-control" id="exampleInputPhone" name="phone" value={this.state.phone} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputEmail">Email</label>
                                            <input type="email" className="form-control" id="exampleInputEmail" name="email" value={this.state.email} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputAddress">Address</label>
                                            <input type="text" className="form-control" id="exampleInputAddress" name="address" value={this.state.address} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        
                                        <div className="form-group">
                                            <label htmlFor="formFileMultiple" className="form-label">Avatar</label>
                                            <input className="form-control" type="file" id="formFileMultiple" name="avatar" value={this.state.avatar} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputCategory">Category ID</label>
                                            <input type="number" className="form-control" id="exampleInputCategory" name="service_category_id" value={this.state.service_category_id} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleDescription">Description</label>
                                            <textarea className="form-control" id="exampleDescription" name="description" value={this.state.description} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <button type="submit" className="btn btn-gradient-primary mr-2">Add</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div> 
        );
    }
}

export default AddVendor;
