import React, { Component } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import SideBar from '../components/SideBar/SideBar';
import axios from 'axios';
import {storage} from '../firebase/index';
class UpdateProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:"",
            phone: "",
            address: "",
            url_avatar: "",
            description: "",
            url_document: "",
            service_category_id:1,
            image1:[],
            image2:[]
        }
        var retrievedData = localStorage.getItem("User");
        var user = JSON.parse(retrievedData);
        this.state.id = user[0].id;
        this.handleUpload = this.handleUpload.bind(this);
        this.handleUpload2 = this.handleUpload2.bind(this);
    }
    handleUpload = () => {
        const uploadTask = storage.ref(`images/${this.state.image1.name}`).put(this.state.image1);
        uploadTask.on('state_changed', 
            () => {
            // complete function ....
            storage.ref('images').child(this.state.image1.name).getDownloadURL().then(url => {
                this.state.url_avatar = url;
            })
        });        
    }

    handleUpload2 = () => {
        const uploadTask = storage.ref(`images/${this.state.image2.name}`).put(this.state.image2);
        uploadTask.on('state_changed',  
            () => {
            // complete function ....
            storage.ref('images').child(this.state.image2.name).getDownloadURL().then(url => {
                this.state.url_document = url;
                alert("Upload");
            })
        });
    }

    submit = (event) => {
        event.preventDefault();
        alert("Active");
        this.handleUpload2();
    }

    render() {
        return (
            <div className="container-scroller">
            <Header/>
            <div className="container-fluid page-body-wrapper">
                <SideBar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="page-header">
                            <h3 className="page-title">
                                <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                <i className="mdi mdi-basket menu-icon" />
                                </span> Cập Nhật Thông Tin Nhà Hàng
                            </h3>
                        </div>
                        <div className="card">
                            <div className="card-body">
                                <form className="forms-sample" onSubmit={this.submit}>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputPhone">Số điện thoại</label>
                                        <input type="tel" required className="form-control" id="exampleIn putPhone" name="phone" onChange={e=>this.state.phone= e.target.value}/> 
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="exampleInputName1">Địa chỉ</label>
                                        <input type="text" required className="form-control" id="exampleIn putName1" name="address" onChange={e=>this.state.address= e.target.value}/> 
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleDescription">Giới thiệu ngắn</label>
                                        <textarea className="form-control" required id="exampleDescription" name="description" onChange={e=>this.state.description= e.target.value}/>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="inputState">Loại sản phẩm</label>
                                        <select class="form-control" name="service_category_id" onChange={e=>this.state.service_category_id= e.target.value}>
                                            <option value="2" >Bánh kem</option>
                                            <option value="1" >Loa máy</option>
                                            <option value="3" >Trang trí</option>
                                            <option value="4" >Thức ăn</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="formFileMultiple" className="form-label">Hình ảnh đại diện</label><br></br>
                                        <input className="form-control" name="image" required type="file" onChange={e=>this.state.image1= e.target.files[0]}/>
                                        {/* <button type="submit" onClick={this.handleUpload} className="btn btn-gradient-primary mr-2">Tải ảnh lên</button> */}
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="formFileMultiple" className="form-label">Giấy chứng nhận/chứng chỉ về VSATTP</label><br></br>
                                        <input className="form-control" name="image" required type="file" onChange={e=>this.state.image2= e.target.files[0]}/>
                                        {/* <button type="submit" onClick={this.handleUpload2} className="btn btn-gradient-primary mr-2">Tải ảnh lên</button> */}
                                    </div>

                                    <button type="submit" className="btn btn-gradient-primary mr-2">Cập nhật</button>
                                </form>
                            </div>
                        </div>
                    </div>
                    <Footer/>
                </div>
            </div>
        </div>
        );
    }
}
export default UpdateProfile;
