import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
class EditVendor extends Component {
    constructor(props) {
        super(props)
        this.state = {
           name: this.props.location.state.name,
           phone: this.props.location.state.phone,
           email: this.props.location.state.email,
           address: this.props.location.state.address,
           avatar: "",
           avatar1: this.props.location.state.avatar,
           description: this.props.location.state.description,
           service_category_id: this.props.location.state.service_category_id
        }
    }
    onChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        if (nam === "img") {
            val = "image/" + event.target.files[0].name;
        }
        this.setState({ [nam]: val });
    }

    submit = (event) => {
        event.preventDefault();
        if(this.state.avatar === ""){
            this.state.avatar = this.state.avatar1;
        }
        let vendor = {
            name: this.state.name,
            phone: this.state.phone,
            email: this.state.email,
            address: this.state.address,
            avatar: this.state.avatar,
            description: this.state.description,
            service_category_id: this.state.service_category_id
        }
        axios.patch('admin_vendor/'+this.props.location.state.id,vendor)
            .then((res) => {
                alert("Cập nhật thành công!");
                console.log(res);
                this.props.history.push("/vendor");
            }).catch((error) => {
                console.log(error);
                alert("Vui lòng cập nhật một vài thông tin!");
            })
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Quản Lí Nhà Cung Cấp - Cập Nhật
                                </h3>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <form className="forms-sample"onSubmit={this.submit}>
                                        <center><img src="https://firebasestorage.googleapis.com/v0/b/fir-queenparty.appspot.com/o/67228025.jpg?alt=media&token=25d38757-06f1-4502-9467-adb9576e5e9b" alt="" width={250} height={250}/></center>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputName1">Tên</label>
                                            <input type="text" className="form-control" id="exampleInputName1" name="name" value={this.state.name} onChange={(e) => this.onChangeHandler(e)}/> 
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputPhone">Số điện thoại</label>
                                            <input type="text" className="form-control" id="exampleInputPhone" name="phone" value={this.state.phone} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputEmail">Email</label>
                                            <input type="email" className="form-control" id="exampleInputEmail" name="email" value={this.state.email} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputAddress">Địa chỉ</label>
                                            <input type="text" className="form-control" id="exampleInputAddress" name="address" value={this.state.address} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        
                                        <div className="form-group">
                                            <label htmlFor="formFileMultiple" className="form-label">Ảnh nhà cung cấp</label>
                                            <input className="form-control" type="file" id="formFileMultiple" name="avatar" value={this.state.avatar} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="inputState">Loại sản phẩm cung cấp</label>
                                            <select class="form-control" onChange={(e) => this.onChangeHandler(e)} name="service_category_id" value={this.state.service_category_id}>
                                                <option value="2" >Bánh kem</option>
                                                <option value="1" >Loa máy</option>
                                                <option value="3" >Trang trí</option>
                                                <option value="4" >Thức ăn</option>
                                            </select>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleDescription">Mô tả</label>
                                            <textarea className="form-control" id="exampleDescription" name="description" value={this.state.description} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <button type="submit" className="btn btn-gradient-primary mr-2">Cập nhật</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div> 
        );
    }
}

export default EditVendor;
