import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
class AddProduct extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: "",
            picture: "",
            price: "",
            discount:"",
            description:"",
            service_category_id:1,
            vendor_id: 1
        }
    }

    onChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        if (nam === "img") {
            val = "image/" + event.target.files[0].name;
        }
        this.setState({ [nam]: val });
    }

    submit = (event) => {
        event.preventDefault();
        alert(this.state.service_category_id);
        let product = {
            name: this.state.name,
            picture: this.state.picture,
            price: this.state.price,
            discount: this.state.discount,
            description: this.state.description,
            service_category_id: this.state.service_category_id,
            vendor_id: this.state.vendor_id
        }
        console.log(product.service_category_id);
        axios.post('http://127.0.0.1:8000/api/admin_product/',product)
            .then((res) => {
                alert("Add successfully!");
                console.log(res);
                this.props.history.push("/product");
            }).catch((error) => {
                console.log(error)
            })
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Product Management
                                </h3>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <h4 className="card-title">Add New Product</h4>
                                    <form className="forms-sample"onSubmit={this.submit}>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputName1">Name</label>
                                            <input type="text" className="form-control" id="exampleInputName1" name="name" value={this.state.name} onChange={(e) => this.onChangeHandler(e)}/> 
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputPrice">Price</label>
                                            <input type="number" className="form-control" id="exampleInputPrice" name="price" value={this.state.price} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleInputDiscount">Discount</label>
                                            <input type="number" className="form-control" id="exampleInputDiscount" name="discount" value={this.state.discount} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                       <div className="form-group">
                                            <label htmlFor="inputState">Loại sản phẩm</label>
                                            <select class="form-control" onChange={(e) => this.onChangeHandler(e)} name="service_category_id" value={this.state.service_category_id}>
                                                <option value="2" >Bánh kem</option>
                                                <option value="1" >Loa máy</option>
                                                <option value="3" >Trang trí</option>
                                                <option value="4" >Thức ăn</option>
                                            </select>
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="inputState">Nhà cung cấp</label>
                                            <select class="form-control" onChange={(e) => this.onChangeHandler(e)} name="vendor_id" value={this.state.vendor_id}>
                                                <option value="2" >Citron Restaurant</option>
                                                <option value="1" >Bếp Cuốn Đà Nẵng</option>
                                                <option value="3" >Nhà hàng 3</option>
                                                <option value="4" >Nhà hàng 4</option>
                                            </select>
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="formFileMultiple" className="form-label">Picture</label>
                                            <input className="form-control" type="file" id="formFileMultiple" name="picture" value={this.state.picture} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="exampleDescription">Description</label>
                                            <textarea className="form-control" id="exampleDescription" name="description" value={this.state.description} onChange={(e) => this.onChangeHandler(e)}/>
                                        </div>
                                        <button type="submit" className="btn btn-gradient-primary mr-2">Add</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div> 
        );
    }
}

export default AddProduct;
