import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
import {storage} from '../../firebase';

class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
        name: "",
        price: "",
        discount:"",
        description:"",
        service_category_id:1,
        vendor_id: 1,
        image: null,
        url: '',
        progress: 0
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleUpload = this.handleUpload.bind(this);
  }
  handleChange = (e) => {
    this.setState({ [nam]: val });
    let nam = e.target.name;
    let val = e.target.value;
    if (nam === "image") {
        val = e.target.files[0];
    }
    this.setState({ [nam]: val });
    // if (e.target.files[0]) {
    //   const image = e.target.files[0];
    //   this.setState(() => ({image}));
    // }
  }
  handleUpload = (event) => {
        event.preventDefault();
        const {image} = this.state;
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        uploadTask.on('state_changed', 
        (snapshot) => {
            // progrss function ....
            const progress = Math.round((snapshot.bytesTransferred / snapshot.totalBytes) * 100);
            this.setState({progress});
        }, 
        (error) => {
            // error function ....
            console.log(error);
        }, 
        () => {
        // complete function ....
        storage.ref('images').child(image.name).getDownloadURL().then(url => {
            this.setState({url});
        })
    });

    let product = {
        name: this.state.name,
        picture: this.state.url,
        price: this.state.price,
        discount: this.state.discount,
        description: this.state.description,
        service_category_id: this.state.service_category_id,
        vendor_id: this.state.vendor_id
    }

    console.log(product);
    // axios.post('http://127.0.0.1:8000/api/admin_product/',product)
    //     .then((res) => {
    //         alert("Thêm sản phẩm thành công!");
    //         console.log(res);
            
    //         this.props.history.push("/product");
    //     }).catch((error) => {
    //         alert("Vui lòng điền thông tin!");
    //         console.log(error);
    //         console.log(product);
    //     })
  }
  render() {
    return (
        <div className="container-scroller">
            <Header/>
            <div className="container-fluid page-body-wrapper">
                <SideBar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="page-header">
                            <h3 className="page-title">
                                <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                <i className="mdi mdi-basket menu-icon" />
                                </span> Quản lí sản phẩm - Thêm mới sản phẩm
                            </h3>
                        </div>
                        <div className="card">
                            <div className="card-body">
                                <form className="forms-sample"onSubmit={this.handleUpload}>
                                    {/* <div className="form-group">
                                        <label htmlFor="exampleInputName1">Tên sản phẩm</label>
                                        <input type="text" required className="form-control" id="exampleInputName1" name="name" value={this.state.name} onChange={(e) => this.handleChange(e)}/> 
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputPrice">Giá sản phẩm</label>
                                        <input type="number" required className="form-control" id="exampleInputPrice" name="price" value={this.state.price} onChange={(e) => this.handleChange(e)}/>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputDiscount">Khuyến mãi</label>
                                        <input type="number" required className="form-control" id="exampleInputDiscount" name="discount" value={this.state.discount} onChange={(e) => this.handleChange(e)}/>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="inputState">Loại sản phẩm</label>
                                        <select class="form-control" onChange={(e) => this.handleChange(e)} name="service_category_id" value={this.state.service_category_id}>
                                            <option value="2" >Bánh kem</option>
                                            <option value="1" >Loa máy</option>
                                            <option value="3" >Trang trí</option>
                                            <option value="4" >Thức ăn</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="inputState">Nhà cung cấp</label>
                                        <select class="form-control" onChange={(e) => this.handleChange(e)} name="vendor_id" value={this.state.vendor_id}>
                                            <option value="2" >Citron Restaurant</option>
                                            <option value="1" >Bếp Cuốn Đà Nẵng</option>
                                            <option value="3" >Memory Lounge</option>
                                            <option value="4" >Nhà hàng Mộc Sea Food</option>
                                        </select>
                                    </div> */}
                                    <div className="form-group">
                                        <label htmlFor="formFileMultiple" className="form-label">Ảnh sản phẩm</label><br></br>
                                        <progress value={this.state.progress} max="100"/>
                                        <input className="form-control" name="image" required type="file" onChange={this.handleChange} value={this.state.image}/>
                                    </div>
                                    {/* <div className="form-group">
                                        <label htmlFor="exampleDescription">Mô tả</label>
                                        <textarea className="form-control" required id="exampleDescription" name="description" value={this.state.description} onChange={(e) => this.handleChange(e)}/>
                                    </div> */}
                                    <button type="submit" className="btn btn-gradient-primary mr-2">Thêm</button>
                                </form>
                            </div>
                        </div>
                    </div>
                    <Footer/>
                </div>
            </div>
        </div> 
    )
  }
}

export default AddProduct;
