import React, { PureComponent } from 'react';
import { PieChart, Pie, Tooltip} from 'recharts';
import axios from 'axios';
export default class ChartSection extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
        ChartSection: []
    };
}

componentDidMount() {
    axios.get('http://127.0.0.1:8000/api/chart_section')
    .then(res => {
        this.setState({
          ChartSection: res.data
        });
        console.log(this.state.ChartSection.totalLoamay)
    })
    .catch((error) => {
    console.log(error);
    })
}

  render() {
    return (
            <PieChart width={300} height={300}>
            <Pie
                dataKey="value"
                isAnimationActive={false}
                data={
                  [
                    { name: 'Loa Máy', value: 56},
                    { name: 'Bánh Kem', value: 3},
                    { name: 'Đồ Trang Trí', value: 3},
                    { name: 'Thức Ăn', value: 300},
                  ]
                }
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                label
            />
            <Tooltip />
            </PieChart>
    );
  }
}
