import React, { PureComponent } from 'react';
import { PieChart, Pie, Tooltip} from 'recharts';
import axios from 'axios';
export default class ChartSection extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
        ChartSection: []
    };
}

componentDidMount() {
    axios.get('https://queen-party-be.herokuapp.com/api/chart_section')
    .then(res => {
        this.setState({
          ChartSection: res.data
        });
        console.log(this.state.ChartSection.totalLoamay)
    })
    .catch((error) => {
    console.log(error);
    })
}

  render() {
    const data = [
      {
        "name": "Page A",
        "uv": 4000,
        "pv": 2400,
        "amt": 2400
      },
      {
        "name": "Page B",
        "uv": 3000,
        "pv": 1398,
        "amt": 2210
      },
      {
        "name": "Page C",
        "uv": 2000,
        "pv": 9800,
        "amt": 2290
      },
      {
        "name": "Page D",
        "uv": 2780,
        "pv": 3908,
        "amt": 2000
      },
      {
        "name": "Page E",
        "uv": 1890,
        "pv": 4800,
        "amt": 2181
      },
      {
        "name": "Page F",
        "uv": 2390,
        "pv": 3800,
        "amt": 2500
      },
      {
        "name": "Page G",
        "uv": 3490,
        "pv": 4300,
        "amt": 2100
      }
    ]
    return (
          <div className="card">
            {/* <PieChart width={300} height={300}>
              <Pie
                dataKey="value"
                isAnimationActive={false}
                data={
                  [
                    { name: 'Loa Máy', value: this.state.ChartSection.totalLoamay},
                    { name: 'Bánh Kem', value: this.state.ChartSection.totalBanhkem},
                    { name: 'Đồ Trang Trí', value: this.state.ChartSection.totalTrangtri},
                    { name: 'Thức Ăn', value: this.state.ChartSection.totalThucan}
                  ]
                }
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                label
              />
            <Tooltip />
            </PieChart> */}
            <ComposedChart width={730} height={250} data={data}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <CartesianGrid stroke="#f5f5f5" />
              <Area type="monotone" dataKey="amt" fill="#8884d8" stroke="#8884d8" />
              <Bar dataKey="pv" barSize={20} fill="#413ea0" />
              <Line type="monotone" dataKey="uv" stroke="#ff7300" />
            </ComposedChart>
          </div>
    );
  }
}
