import React, { PureComponent } from 'react';
import { PieChart, Pie, Tooltip} from 'recharts';
import axios from 'axios';
export default class ChartSection extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
        ChartSection: []
    };
    this.data01 = [];
}

componentDidMount() {
    axios.get('http://127.0.0.1:8000/api/chart_section')
    .then(res => {
        this.setState({
          ChartSection: res.data
        });
        this.data01 = [
          { name: 'Loa Máy', value: this.state.ChartSection.totalLoamay},
          { name: 'Bánh Kem', value: this.state.ChartSection.totalLoamay},
          { name: 'Đồ Trang Trí', value: this.state.ChartSection.totalLoamay},
          { name: 'Thức Ăn', value: this.state.ChartSection.totalLoamay}
        ];
    })
    .catch((error) => {
    console.log(error);
    })
}

  render() {
    return (
            <PieChart width={300} height={300}>
            <Pie
                dataKey="value"
                isAnimationActive={false}
                data={this.data01}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                label
            />
            <Tooltip />
            </PieChart>
    );
  }
}
