import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
import { Link } from 'react-router-dom';
class Vendor extends Component {
    constructor(props) {
        super(props)
        this.state = {
          vendors: []
        };
    }
    
    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/admin_vendor')
        .then(res => {
            this.setState({
                vendors: res.data.data
            });
        })
        .catch((error) => {
        console.log(error);
        })
    }
    
    deleteVendor(id){
        axios.delete('http://127.0.0.1:8000/api/admin_vendor/'+id)
            .then((res) => {
                alert("Delete successfully!");
                console.log(res);
                this.componentDidMount();
            }).catch((error) => {
                console.log(error)
            })
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Quản Lí Nhà Cung Cấp
                                </h3>
                                <nav aria-label="breadcrumb">
                                    <ul className="breadcrumb">
                                        <li className="breadcrumb-item active" aria-current="page">
                                            <span />Tổng Quan <i className="mdi mdi-alert-circle-outline icon-sm text-primary align-middle" />
                                        </li>
                                    </ul>
                                </nav>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <div className="page-header">
                                        <h5 className="page-title">
                                            Danh sách nhà cung cấp
                                        </h5>
                                        <nav aria-label="breadcrumb">
                                            <Link to="/add_vendor" className="nav-link">
                                                <h5 className="page-title">
                                                    Thêm mới
                                                </h5>
                                            </Link>
                                        </nav>
                                    </div>
                                    <table className="table table-striped">
                                    <thead>
                                        <tr>
                                        <th> ID </th>
                                        <th> Tên </th>
                                        <th> Số điện thoại </th>
                                        <th> Email </th>
                                        <th> Tác vụ </th>
                                        </tr>
                                    </thead>
                                        <tbody>
                                            {
                                                this.state.vendors.map((item,index)=>(
                                                    <tr key={index}>
                                                        <td>{item.id}</td>
                                                        <td>{item.name}</td>
                                                        <td>{item.phone}</td>
                                                        <td>{item.email}</td>
                                                        <td><span>
                                                                <button type="submit" onClick={()=>this.deleteVendor(item.id)} className="btn btn-gradient-primary btn-rounded btn-icon"><i className="mdi mdi-delete" /></button>
                                                                <button type="submit" className="btn btn-gradient-primary btn-rounded btn-icon">
                                                                    <Link to={{pathname:'/edit_vendor', state: {id:item.id, name:item.name, phone:item.phone, email:item.email, address: item.address, avatar: item.avatar, description: item.description, service_category_id:item.service_category_id}}} className="nav-link">
                                                                        <i className="mdi mdi-pot-mix" />
                                                                    </Link>
                                                                </button>
                                                            </span>
                                                        </td>
                                                    </tr>
                                                ))
                                            }
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div>  
        );
    }
}

export default Vendor;
