import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
import { Link } from 'react-router-dom';
class Product extends Component {
    constructor(props) {
        super(props)
        this.state = {
          products: []
        };
    }
    
    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/admin_product')
        .then(res => {
            this.setState({
                products: res.data
            });
        })
        .catch((error) => {
        console.log(error);
        })
    }
    
    deleteProduct(id){
        return(event)=> {
            event.preventDefault();
            axios.delete('http://127.0.0.1:8000/api/admin_product/'+this.props.location.state.id,product)
            .then((res) => {
                alert("Delete successfully!");
                console.log(res);
                this.props.history.push("/product");
            }).catch((error) => {
                console.log(error)
            })
        }
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Product Management
                                </h3>
                                <nav aria-label="breadcrumb">
                                    <ul className="breadcrumb">
                                        <li className="breadcrumb-item active" aria-current="page">
                                            <span />Overview <i className="mdi mdi-alert-circle-outline icon-sm text-primary align-middle" />
                                        </li>
                                    </ul>
                                </nav>
                            </div>
                            <div className="card">
                                <div className="card-body">
                                    <div className="page-header">
                                        <h5 className="page-title">
                                            List Products
                                        </h5>
                                        <nav aria-label="breadcrumb">
                                            <Link to="/add_product" className="nav-link">
                                                <h5 className="page-title">
                                                    Add new Product
                                                </h5>
                                            </Link>
                                        </nav>
                                    </div>
                                    <table className="table table-striped">
                                    <thead>
                                        <tr>
                                        <th> ID </th>
                                        <th> Name </th>
                                        <th> Discount </th>
                                        <th> Price </th>
                                        <th> Category </th>
                                        <th> Vendor </th>
                                        <th> Action </th>
                                        </tr>
                                    </thead>
                                        <tbody>
                                            {
                                                this.state.products.map((item,index)=>(
                                                    <tr key={index}>
                                                        <td>{index}</td>
                                                        <td>{item.name}</td>
                                                        <td>{item.discount} $</td>
                                                        <td>{item.price} $</td>
                                                        <td>{item.service_category_id} $</td>
                                                        <td>{item.vendor_id} $</td>
                                                        <td><span>
                                                                <button type="submit" onClick={()=>this.deleteProduct(item.id)} className="btn btn-gradient-primary btn-rounded btn-icon"><i className="mdi mdi-delete" /></button>
                                                                <button type="submit" className="btn btn-gradient-primary btn-rounded btn-icon">
                                                                    <Link to={{pathname:'/edit_product', state: {id:item.id, name:item.name, discount:item.discount, price:item.price, service_category_id: item.service_category_id, vendor_id: item.vendor_id, description: item.description, picture:item.picture}}} className="nav-link">
                                                                        <i className="mdi mdi-pot-mix" />
                                                                    </Link>
                                                                </button>
                                                            </span>
                                                        </td>
                                                    </tr>
                                                ))
                                            }
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div> 
        );
    }
}

export default Product;
