import React, { Component } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import SideBar from '../components/SideBar/SideBar';
import axios from 'axios';
import {storage} from '../firebase/index';
class UpdateProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:"",
            name: "",
            phone: "",
            email: "",
            address: "",
            url_avatar: "",
            description: "",
            url_document: "",
            service_category_id: "",
            image1: null,
            progress1: 0,
            progress2: 0
        }
        var retrievedData = localStorage.getItem("User");
        var user = JSON.parse(retrievedData);
        this.state.id = user[0].id;
        this.state.name = user[0].name;
        this.state.email = user[0].email;
        this.handleUpload = this.handleUpload.bind(this);
        this.handleChange1 = this.handleChange2.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
    }
    handleChange1 = e => {
        const image1 = e.target.files[0];
        this.state.image1 = image1;
    }

    handleChange2 = e => {
        const image2 = e.target.files[0];
        this.setState(() => ({image2}));
    }
    handleUpload = (event) => {
        event.preventDefault();
        const {image} = this.state.image1;
        console.log(image);
        // const uploadTask = storage.ref(`images/${image.name}`).put(image);
        // uploadTask.on('state_changed', 
        //     (snapshot) => {
        //         // progrss function ....
        //         const progress = Math.round((snapshot.bytesTransferred / snapshot.totalBytes) * 100);
        //         this.setState({progress});
        //     }, 
        //     (error) => {
        //         // error function ....
        //         console.log(error);
        //     }, 
        //     () => {
        //     // complete function ....
        //     storage.ref('images').child(image.name).getDownloadURL().then(url => {
        //         this.setState({url});
        //     })
        // });
    }

    render() {
        return (
            <div className="container-scroller">
            <Header/>
            <div className="container-fluid page-body-wrapper">
                <SideBar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="page-header">
                            <h3 className="page-title">
                                <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                <i className="mdi mdi-basket menu-icon" />
                                </span> Cập Nhật Thông Tin Nhà Hàng
                            </h3>
                        </div>
                        <div className="card">
                            <div className="card-body">
                                <form className="forms-sample">
                                    <div className="form-group">
                                        <label htmlFor="exampleInputName1">Tên nhà hàng</label>
                                        <input type="text" required className="form-control" id="exampleIn putName1" name="name" value={this.state.name}/> 
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="exampleInputPhone">Số điện thoại</label>
                                        <input type="tel" required className="form-control" id="exampleIn putPhone" name="phone" value={this.state.phone}/> 
                                    </div>

                                    <div className="form-group">
                                        <input type="email" required className="form-control form-control-lg" id="exampleInputEmail1" placeholder="Email" 
                                        name="email" value={this.state.email} onChange={e=>this.state.email= e.target.value}/>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="exampleInputName1">Địa chỉ</label>
                                        <input type="text" required className="form-control" id="exampleIn putName1" name="address" value={this.state.address}/> 
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleDescription">Giới thiệu ngắn</label>
                                        <textarea className="form-control" required id="exampleDescription" name="description" value={this.state.description}/>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="inputState">Loại sản phẩm</label>
                                        <select class="form-control" name="service_category_id" value={this.state.service_category_id}>
                                            <option value="2" >Bánh kem</option>
                                            <option value="1" >Loa máy</option>
                                            <option value="3" >Trang trí</option>
                                            <option value="4" >Thức ăn</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="formFileMultiple" className="form-label">Hình ảnh đại diện</label><br></br>
                                        <progress value={this.state.progress1} max="100"/>
                                        <input className="form-control" name="image" required type="file" onChange={this.handleChange1}/>
                                        <button type="submit" onClick={this.handleUpload} className="btn btn-gradient-primary mr-2">Tải ảnh lên</button>
                                    </div>

                                    {/* <div className="form-group">
                                        <progress value={this.state.progress1} max="100"/>
                                        <label htmlFor="formFileMultiple" className="form-label">Giấy chứng nhận/chứng chỉ về VSATTP</label><br></br>
                                        <input className="form-control" name="image" required type="file"/>
                                    </div> */}
                                    <button type="submit" className="btn btn-gradient-primary mr-2">Thêm</button>
                                </form>
                            </div>
                        </div>
                    </div>
                    <Footer/>
                </div>
            </div>
        </div>
        );
    }
}
export default UpdateProfile;
